import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
const SignUp = () => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    useEffect(() => {

        const auth = localStorage.getItem('user');
        if (auth) {
            navigate('/');
        }
    })
    const collectData = async () => {
        console.warn(name, email, password);
        let result = await fetch('http://localhost:5000/registers', {
            method: 'post',
            body: JSON.stringify({ name, email, password }),
            headers: {
                'Content-Type': 'application/json'
            },

        })
        result = await result.json()
        console.warn(result);
        localStorage.setItem("user", JSON.stringify(result));
        navigate('/');

    }
    return (
        <div class =" inline-grid  mt-20">
            <h1 class='text-center font-bold text-lg '>Register</h1>
            <label class="text-left font-bold text-sky-300 mt-8">*Username*</label>
            <input class=" w-56 outline outline-offset-2 outline-1  rounded-md mt-2" type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Enter Name" /><br />
            <label class="text-left font-bold text-sky-300 mt-2">*Email*</label>
            <input class=" peer  w-56 outline outline-offset-2 outline-1  rounded-md mt-2"  type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter Email" /><br />
            <p class=" mt-0 invisible peer-invalid:visible text-pink-600 text-sm">
                Please provide a valid email address.
            </p>
            <label class="text-left font-bold text-sky-300 mt-1">*Password*</label>
            <input class=" w-56 outline outline-offset-2 outline-1  rounded-md mt-2" type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Enter Password" /><br />
            <button onClick={collectData} class=' w-10 outline outline-offset-2 outline-2 text-left mt-8' > Sign Up </button>
        </div>
    )
}

export default SignUp;
